INSERT INTO public.wines (brand, made_in_country, in_stock, price, title, capacity, description, "name", wine_type,
                          event_id, wine_style_id, wine_taste_id,meal_id)
VALUES ('Chateau de la Chapelle', 'France', true, 250.00, 'Chateau de la Chapelle Blaye Cotes de Bordeaux, 2018', 0.75,
        'This unoaked wine is a saturated deep purple-black in colour, with ... aromas of warm blackcurrant and black cherry fruits and sweet, fresh plums. On the palate, it is ... a torrent of pure fruit, soft tannins and juicy acidity.',
        'Blaye Cotes de Bordeaux', 'RED', 2, 7, 6, 1),
       ('San Gregorio', 'Spain', true, 488.99, 'San Gregorio El Vergal Tempranillo, 2020', 0.75,
        'Plums and grapes on the nose with hints of vanilla, following through to a medium body, fresh fruit and a bright finish." A perfect food wine, or on its own.',
        'El Vergal Tempranillo', 'RED', 3, 7, 2, 2),
       ('Conde De Caralt', 'Spain', true, 512.77, 'Conde de Caralt Brut NV Cava', 1.5,
        'Grassy citrus aromas rest comfortably on a mild, nonaggressive bouquet. A citrusy mouthfeel isnt overly bubbly, while pithy lime and tangerine flavors finish mild, with tangy acidity and a cleansing quality.',
        'Brut NV Cava', 'CHAMPAGNE_SPARKLING', 3, 3, 40, 3),
       ('Mumm', 'France', true, 433.20, 'GH Mumm Grand Cordon Brut Champagne', 0.75,
        'Toast and dried fruit notes mix in this very harmonious dry Champagne, which is full of flavor- dried apricot, candied orange peel, nut and smoke- but still elegant and light-weight. The rich, toasty note lingers on the vibrant finish.',
        'Grand Cordon Brut Champagne', 'CHAMPAGNE_SPARKLING', 4, 2, 26, 4),
       ('Nicolas Feuillatte', 'France', true, 459.20, 'Nicolas Feuillatte Brut Champagne', 0.75,
        'Rich and creamy, showing the dried fruit, nut and spice notes of fruitcake, with lemon meringue, quince and honey flavors. Clean citrusy acidity integrates beautifully and provides an elegance to this refined Champagne',
        'Brut Champagne', 'CHAMPAGNE_SPARKLING', 1, 3, 37, 5),
       ('Chateau De Nages', 'France', true, 394.50, 'Chateau de Nages Bulles de Nages Extra Brut', 0.75,
        'This sparkling100% Grenache Blanc displays great finesse and amazing freshness. This elegant Extra-Brut wine benefits from lees aging for 6 months and expresses notes of white flowers, pear, and passionfruit, with a lively and lingering finish',
        'Bulles de Nages Extra Brut', 'CHAMPAGNE_SPARKLING', 2, 7, 38, 6),
       ('Marques De Monistrol', 'Spain', true, 350.12, 'Marques de Monistrol Brut Cava', 0.75,
        'An attractive and harmonious example, showcasting pretty aromas of orchid fruit. A delicate autolytic character, and flavours of fresh bread and toasted brioche add an appeling complexity on the palete',
        'Brut Cava', 'CHAMPAGNE_SPARKLING', 2, 9, 23, 6),
       ('Tesoro della Regina', 'Italy', true, 450.00, 'Tesoro della Regina Rose', 0.75,
        'You''ll be transported to the Italian Riviera. Refreshing and light as a summer breeze. Goes well with a crisp Caesar salad, a platter of grilled veggies and friends.',
        'Rose', 'ROSE', 2, 11, 42, 5),
       ('Domaine Loubejac', 'USA', true, 734.22, 'Domaine Loubejac Pinot Noir Willamette Valley, 2020', 0.75,
        'Lots of hibiscus with dried strawberry on the nose and palate. Medium body, firm tannins and a lightly chewy yet polished and fine finish. Drinkable now.',
        'Pinot Noir', 'RED', 3, 7, 18, 4),
       ('Olema', 'USA', true, 240.34, 'Olema Cabernet Sauvignon Sonoma County, 2019', 0.75,
        'A lot of chocolate, coconut and vanilla on the nose with some ripe dark fruit. Some tobacco, too. Medium-bodied, firm and fruity. Delicious now.',
        'Cabernet Sauvignon', 'RED', 4, 8, 2, 3);
